function checkLogin(){let e=document.getElementById("logForm");e.addEventListener("submit",t=>{t.preventDefault();let r=new FormData(e);fetch(`${location.origin}/websignerr/controlers/loginUser.php`,{method:"POST",headers:{"X-Requested-With":"xmlhttprequest"},body:r}).then(e=>e.json()).then(e=>{""!=e.msg?alert(e.msg):e.isOk?location.assign(`${location.href}home`):alert("erreur")})})}checkLogin();let registerForm=document.getElementById("registerForm");registerForm.addEventListener("submit",e=>{e.preventDefault();let t=new FormData(registerForm);fetch(`${location.origin}/websignerr/controlers/registerUser.php`,{method:"POST",headers:{"X-Requested-With":"xmlhttprequest"},body:t}).then(e=>e.json()).then(e=>{console.log(e),""!=e.msg?alert(e.msg):e.isOk?location.assign(`${location.href}home`):alert("erreur")})});